// Bools returns list of bools from selector. It is only allowed when selecting one field.
func (uis *UserInfoSelect) Bools(ctx context.Context) ([]bool, error) {
	if len(uis.fields) > 1 {
		return nil, errors.New("ent: UserInfoSelect.Bools is not achievable when selecting more than 1 field")
	}
	var v []bool
	if err := uis.Scan(ctx, &v); err != nil {
		return nil, err
	}
	return v, nil
}

